lass="row-fluid">        <div class="span12">            <div class="widget-area-5">            <div id="kopa_widget_testimonial-2" class="widget clearfix kp-testimonial">                <div class="wrapper">                     <div class="row-fluid">                         <div class="span12">                             <h4 class="widget-title clearfix"><span>Recent Posts</span></h4>                             <div class="list-carousel responsive" >                        		<?php	function get_popular_postsb(){                                    global $wpdb;                                    $blogs = $wpdb->get_results( "SELECT blog_id FROM {$wpdb->blogs} WHERE blog_id != {$wpdb->blogid} AND site_id = '{$wpdb->siteid}' AND spam = '0' AND deleted = '0' AND archived = '0' order by blog_id LIMIT 9", ARRAY_A);	                                    array_unshift($blogs, 1);                                     /*Including parent blog in the array*/                                    $html = '<ul class="testimonial-slider">';                                      foreach($blogs as $blog) {	                                        switch_to_blog( $blog[ 'blog_id' ] );	                                        $posts = $wpdb->get_results("SELECT * FROM {$wpdb->prefix}posts WHERE post_type='post' AND post_status='publish' AND post_author !=1 Order by ID DESC LIMIT 9");                                        global $blog_id;                                        $current_blog_details = get_blog_details( array( 'blog_id' => $blog_id ) );                                        foreach($posts as $post) {                                              $home=home_url();                                             $html .= '<li style="width: 355px;">                                                <article class="testimonial-item clearfix">                                                 <div class="testimonial-content clearfix">                               