                                    ?>                                                                                                                                                                                       </ul>                                                       <div class="clearfix">                                                                    </div>                                                        <div class="carousel-nav clearfix">                                     <a id="widget-kopa_widget_testimonial-2-prev" class="carousel-prev" href="#">&lt;</a>                                    <a id="widget-kopa_widget_testimonial-2-next" class="carousel-next" href="#">&gt;</a>                                 </div><!--end:carousel-nav-->                              </div><!--end:list-carousel-->                             </div><!--span12-->                      </div><!--row-fluid-->                 </div><!--wrapper-->                </div>                        </div><!--widget-area-5-->        </div><!--span12-->    </div><!--row-fluid-->    <?php//endif;wp_reset_query();?>	     <?phpget_footer();