<?phpglobal $post, $blog_id;while (have_posts()) : the_post();    $post_id = get_the_ID();    $post_url = get_permalink();    $post_title = get_the_title();    $post_format = get_post_format();    $article_classes = array();    $post_format_icon = '&#xe034;';    switch ($post_format) {        case 'audio':            $post_format_icon = '&#xe020;';            break;        case 'video':            $post_format_icon = '&#xe023;';            break;        case 'gallery':            $post_format_icon = '&#xe01c;';            break;        case 'quote':            $post_format_icon = '&#xe075;';            $article_classes[] = 'article-no-thumb';            break;        case 'aside':            $post_format_icon = '&#xe034;';            $article_classes[] = 'article-no-thumb';            break;        default:            $post_format_icon = '&#xe034;';            if (!has_post_thumbnail()) {                $article_classes[] = 'article-no-thumb';            }            break;    }    $article_classes[] = 'clearfix';    ?><script type="text/javascript">    $(document).ready(function(){        $(document).delegate('.remove_from_favorite','click', function(){            var remove_post_id = $('#fav_post_id').val();            var remove_blog_id = $('#fav_blog_id').val();            var remove_user_id = $('#user_id').val();            var remove_query_url = '<?php echo get_template_directory_uri()?>/fav_blogs.php?post_id=' + remove_post_id + '&blog_id=' + remove_blog_id + '&user_id=' + remove_user_id + '&action=remove';            $.post( remove_query_url, function( data ) {                $('.remove_from_favorite').removeClass('remove_from_favorite').addClass('add_to_fav').html(data);            });        });    });</script><script type="text/javascript">    $(document).ready(function(){        $(document).delegate('.add_to_fav','click', function(){            var post_id = $('#fav_post_id').val();            var blog_id = $('#fav_blog_id').val();            var user_id = $('#user_id').val();            var query_url = '<?php echo get_template_directory_uri()?>/fav_blogs.php?post_id=' + post_id + '&blog_id=' + blog_id + '&user_id=' + user_id;            $.post( query_url, function( data ) {                $('.add_to_fav').removeClass('add_to_fav').addClass('remove_from_favorite').html(data);            });        });     });     </script>    <div <?php post_class(); ?>>          <?php        if (!in_array($post_format, array('aside', 'gallery', 'video', 'audio', 'quote')) && has_post_thumbnail()):            ?>            <div class="entry-thumb">                                       <?php the_post_thumbnail('kopa-image-size-2'); ?>            </div>            <?php        endif;        ?>         <header class="entry-header">            <?php if ('aside' !== $post_format): ?>                <h2 class="entry-title"><?php echo $post_title; ?></h2>            <?php endif; ?>            <div class="entry-meta-box">                <div class="entry-meta-box-inner">                    <span class="entry-date"><span class="icon-clock-4 entry-icon"></span><?php echo get_the_date(); ?></span>                    <span class="entry-author"><span class="icon-user entry-icon"></span><?php _e('by:', kopa_get_domain()); ?>&nbsp;<a href="<?php echo get_author_posts_url(get_the_author_meta('ID')); ?>"><?php the_author_meta('nickname'); ?></a></span>                    <?php if (has_category()): ?>                        <span class="entry-category"><span class="icon-book entry-icon"></span><?php _e('in:', kopa_get_domain()); ?>&nbsp;<?php the_category(', '); ?></span>                    <?php endif; ?>                    <span class="entry-comment"><span class="icon-bubbles-4 entry-icon"></span><?php comments_popup_link(__('No Comment', kopa_get_domain()), __('1 Comment', kopa_get_domain()), __('% Comments', kopa_get_domain()), '', __('Comments Off', kopa_get_domain())); ?></span>                                               </div>                <span class="entry-meta-circle"></span>                <span class="entry-meta-icon" data-icon="<?php echo $post_format_icon; ?>"></span>            </div>        </header>               <span style="font-family: AltastGreeting; font-size: 32px; color:#000;" class="blogfont"><?php the_content(); ?></span>        <?php        wp_link_pages(array('before' => '<div class="pagination">', 'after' => '</div>'));        ?>               <footer class="clearfix">            <p class="prev-post">                <?php previous_post_link('%link', __('&laquo;&nbsp;Older Article', kopa_get_domain()), true); ?>                            </p>            <p class="next-post">                <?php next_post_link('%link', __('Next Article&nbsp;&raquo;', kopa_get_domain()), true); ?>                               </p>        </footer>    </div><?php if (! is_user_logged_in() ) {  ?>     <?php kopa_get_socials_link(); ?><?php } else{ ?>    <div class="socials-link-container clearfix">        <input type="hidden" name="fav_post_id" id="fav_post_id" value="<?php echo $post_id;?>">        <input type="hidden" name="fav_blog_id" id="fav_blog_id" value="<?php echo $blog_id;?>">        <input type="hidden" name="user_id" id="user_id" value="<?php echo $user_ID;?>">        <?php            $fav_posts = get_user_meta($user_ID, 'fav_blogs', true);            $fav_posts = unserialize($fav_posts);            if(!empty($fav_posts) && key_exists('fav_'.$blog_id.'_'.$post_id, $fav_posts)){        ?>            <div><a class="remove_from_favorite" href="javascript:void(0);"> REMOVE FROM FAVORITES </a></div>        <?php } else { ?>            <div><a href="javascript:void(0);" class="add_to_fav">ADD TO FAVORITES</a></div>        <?php } ?>    </div><?php } ?>    <?php kopa_get_about_author(); ?>    <?php comments_template(); ?>    <?php kopa_get_facebook_comment(); ?>    <?php kopa_get_related_articles(); ?>    <?phpendwhile;?>